mapper.select:


SET @ROWNUM:= 0;
SELECT * FROM (SELECT @ROWNUM := @ROWNUM +1 ROWNUM, P.* FROM MESSAGE P, (SELECT @ROWNUM := 0) TMP 
				WHERE SUSERID = #{param3} order by p.msindex desc) S 
WHERE ROWNUM 
BETWEEN ${param1}*${param2}-(${param2}-1) AND ${param1}*${param2}



SET @ROWNUM:= 0;
SELECT * FROM (SELECT @ROWNUM := @ROWNUM +1 ROWNUM, P.* FROM MESSAGE P, (SELECT @ROWNUM := 0) TMP  
				WHERE RUSERID = #{param3} order by p.msindex desc ) S 
WHERE ROWNUM 
BETWEEN ${param1}*${param2}-(${param2}-1) AND ${param1}*${param2}
